import React, { useState } from 'react';
import Header from '../../components/layout/Header';
import Footer from '../../components/layout/Footer';

const Messages = () => {
  const [selectedConversation, setSelectedConversation] = useState<number | null>(null);
  const [newMessage, setNewMessage] = useState('');

  // Mock data
  const conversations = [
    {
      id: 1,
      company: 'Nomad',
      jobTitle: 'Senior Frontend Developer',
      lastMessage: 'Thank you for your application. We would like to schedule an interview...',
      time: '2 hours ago',
      unread: true
    },
    {
      id: 2,
      company: 'Discord',
      jobTitle: 'Full Stack Developer',
      lastMessage: 'Your application has been reviewed. We will contact you soon...',
      time: '1 day ago',
      unread: false
    },
    {
      id: 3,
      company: 'GitHub',
      jobTitle: 'Backend Developer',
      lastMessage: 'We regret to inform you that we have decided to move forward with other candidates...',
      time: '3 days ago',
      unread: false
    }
  ];

  const messages = [
    {
      id: 1,
      sender: 'Nomad HR',
      content: 'Thank you for your application. We would like to schedule an interview with you next week.',
      time: '2 hours ago',
      isUser: false
    },
    {
      id: 2,
      sender: 'You',
      content: 'Thank you for the opportunity. I am available for an interview next week.',
      time: '1 hour ago',
      isUser: true
    },
    {
      id: 3,
      sender: 'Nomad HR',
      content: 'Great! How about Tuesday at 2 PM?',
      time: '30 minutes ago',
      isUser: false
    }
  ];

  const handleSendMessage = () => {
    if (newMessage.trim()) {
      // Here you would typically send the message to your backend
      console.log('Sending message:', newMessage);
      setNewMessage('');
    }
  };

  return (
    <>
      <Header />
      <div className="min-h-screen bg-gray-50 py-8">
        <div className="container mx-auto px-4">
          <h1 className="text-3xl font-bold text-gray-900 mb-8">Messages</h1>
          
          <div className="flex gap-6">
            {/* Conversations List */}
            <div className="w-1/3 bg-white rounded-lg shadow">
              <div className="p-4 border-b">
                <input
                  type="text"
                  placeholder="Search messages..."
                  className="w-full px-4 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-emerald-500"
                />
              </div>
              <div className="divide-y">
                {conversations.map((conversation) => (
                  <div
                    key={conversation.id}
                    className={`p-4 cursor-pointer hover:bg-gray-50 ${
                      selectedConversation === conversation.id ? 'bg-emerald-50' : ''
                    }`}
                    onClick={() => setSelectedConversation(conversation.id)}
                  >
                    <div className="flex justify-between items-start">
                      <div>
                        <h3 className="font-semibold text-gray-900">{conversation.company}</h3>
                        <p className="text-sm text-gray-600">{conversation.jobTitle}</p>
                      </div>
                      <span className="text-xs text-gray-500">{conversation.time}</span>
                    </div>
                    <p className="mt-2 text-sm text-gray-600 truncate">{conversation.lastMessage}</p>
                    {conversation.unread && (
                      <span className="inline-block mt-2 px-2 py-1 text-xs font-medium text-white bg-emerald-600 rounded-full">
                        New
                      </span>
                    )}
                  </div>
                ))}
              </div>
            </div>

            {/* Messages Area */}
            <div className="flex-1 bg-white rounded-lg shadow">
              {selectedConversation ? (
                <>
                  <div className="p-4 border-b">
                    <h2 className="text-xl font-semibold text-gray-900">
                      {conversations.find(c => c.id === selectedConversation)?.company}
                    </h2>
                    <p className="text-sm text-gray-600">
                      {conversations.find(c => c.id === selectedConversation)?.jobTitle}
                    </p>
                  </div>
                  <div className="p-4 h-[500px] overflow-y-auto">
                    {messages.map((message) => (
                      <div
                        key={message.id}
                        className={`mb-4 ${
                          message.isUser ? 'text-right' : 'text-left'
                        }`}
                      >
                        <div
                          className={`inline-block p-3 rounded-lg ${
                            message.isUser
                              ? 'bg-emerald-100 text-emerald-900'
                              : 'bg-gray-100 text-gray-900'
                          }`}
                        >
                          <p className="text-sm">{message.content}</p>
                          <span className="text-xs text-gray-500 mt-1 block">
                            {message.time}
                          </span>
                        </div>
                      </div>
                    ))}
                  </div>
                  <div className="p-4 border-t">
                    <div className="flex gap-2">
                      <input
                        type="text"
                        value={newMessage}
                        onChange={(e) => setNewMessage(e.target.value)}
                        placeholder="Type your message..."
                        className="flex-1 px-4 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-emerald-500"
                      />
                      <button
                        onClick={handleSendMessage}
                        className="px-4 py-2 bg-emerald-600 text-white rounded-lg hover:bg-emerald-700"
                      >
                        Send
                      </button>
                    </div>
                  </div>
                </>
              ) : (
                <div className="h-full flex items-center justify-center text-gray-500">
                  Select a conversation to start messaging
                </div>
              )}
            </div>
          </div>
        </div>
      </div>
      <Footer />
    </>
  );
};

export default Messages;
