import React, { useState } from 'react';
import { useSearchParams, Link } from 'react-router-dom';

interface Industry {
  name: string;
  count: number;
}

interface CompanySize {
  range: string;
  count: number;
}

const industries: Industry[] = [
  { name: 'Advertising', count: 43 },
  { name: 'Business Service', count: 4 },
  { name: 'Blockchain', count: 5 },
  { name: 'Cloud', count: 15 },
  { name: 'Consumer Tech', count: 5 },
  { name: 'Education', count: 34 },
  { name: 'Fintech', count: 45 },
  { name: 'Gaming', count: 33 },
  { name: 'Food & Beverage', count: 5 },
  { name: 'Healthcare', count: 3 },
  { name: 'Hosting', count: 8 },
  { name: 'Media', count: 4 }
];

const companySizes: CompanySize[] = [
  { range: '1-50', count: 25 },
  { range: '51-150', count: 57 },
  { range: '151-250', count: 45 },
  { range: '251-500', count: 4 },
  { range: '501-1000', count: 43 },
  { range: '1000 - above', count: 23 }
];

const CompanySearch = () => {
  const [searchParams] = useSearchParams();
  const [selectedIndustries, setSelectedIndustries] = useState<string[]>([]);
  const [selectedSizes, setSelectedSizes] = useState<string[]>([]);

  const query = searchParams.get('q') || '';
  const location = searchParams.get('location') || '';

  const handleIndustryChange = (industry: string) => {
    setSelectedIndustries(prev =>
      prev.includes(industry)
        ? prev.filter(i => i !== industry)
        : [...prev, industry]
    );
  };

  const handleSizeChange = (size: string) => {
    setSelectedSizes(prev =>
      prev.includes(size)
        ? prev.filter(s => s !== size)
        : [...prev, size]
    );
  };

  return (
    <div className="min-h-screen bg-white py-8">
      <div className="container mx-auto max-w-7xl px-4">
        {/* Hero Section */}
        <div className="text-center mb-8">
          <h1 className="text-4xl font-bold mb-2">
            Find your <span className="text-emerald-400">dream companies</span>
          </h1>
          <p className="text-gray-600">
            Find the dream companies you dream work for
          </p>
        </div>

        {/* Search Section */}
        <div className="mb-8">
          <form className="flex gap-4">
            <div className="flex-1">
              <div className="relative">
                <div className="absolute inset-y-0 left-0 pl-3 flex items-center pointer-events-none">
                  <svg className="h-5 w-5 text-gray-400" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M21 21l-6-6m2-5a7 7 0 11-14 0 7 7 0 0114 0z" />
                  </svg>
                </div>
                <input
                  type="text"
                  defaultValue={query}
                  placeholder="Company name or keyword"
                  className="block w-full pl-10 pr-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-emerald-500 focus:border-emerald-500"
                />
              </div>
            </div>
            <div className="w-64">
              <div className="relative">
                <div className="absolute inset-y-0 left-0 pl-3 flex items-center pointer-events-none">
                  <svg className="h-5 w-5 text-gray-400" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M17.657 16.657L13.414 20.9a1.998 1.998 0 01-2.827 0l-4.244-4.243a8 8 0 1111.314 0z" />
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M15 11a3 3 0 11-6 0 3 3 0 016 0z" />
                  </svg>
                </div>
                <input
                  type="text"
                  defaultValue={location}
                  className="block w-full pl-10 pr-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-emerald-500 focus:border-emerald-500"
                />
              </div>
            </div>
            <button
              type="submit"
              className="px-6 py-2 bg-emerald-500 text-white rounded-lg hover:bg-emerald-600 transition-colors"
            >
              Search
            </button>
          </form>
          <div className="mt-2">
            <span className="text-gray-600 text-sm">Popular: </span>
            <span className="text-gray-500 text-sm">Twitter, Microsoft, Apple, Facebook</span>
          </div>
        </div>

        {/* Content Section */}
        <div className="flex gap-8">
          {/* Filters */}
          <div className="w-64 flex-shrink-0">
            {/* Industry Filter */}
            <div className="mb-6">
              <h3 className="text-sm font-medium text-gray-900 mb-4">Industry</h3>
              <div className="space-y-2">
                {industries.map((industry) => (
                  <label key={industry.name} className="flex items-center">
                    <input
                      type="checkbox"
                      checked={selectedIndustries.includes(industry.name)}
                      onChange={() => handleIndustryChange(industry.name)}
                      className="h-4 w-4 text-emerald-600 focus:ring-emerald-500 border-gray-300 rounded"
                    />
                    <span className="ml-2 text-sm text-gray-600">{industry.name}</span>
                    <span className="ml-auto text-sm text-gray-400">({industry.count})</span>
                  </label>
                ))}
              </div>
            </div>

            {/* Company Size Filter */}
            <div>
              <h3 className="text-sm font-medium text-gray-900 mb-4">Company Size</h3>
              <div className="space-y-2">
                {companySizes.map((size) => (
                  <label key={size.range} className="flex items-center">
                    <input
                      type="checkbox"
                      checked={selectedSizes.includes(size.range)}
                      onChange={() => handleSizeChange(size.range)}
                      className="h-4 w-4 text-emerald-600 focus:ring-emerald-500 border-gray-300 rounded"
                    />
                    <span className="ml-2 text-sm text-gray-600">{size.range}</span>
                    <span className="ml-auto text-sm text-gray-400">({size.count})</span>
                  </label>
                ))}
              </div>
            </div>
          </div>

          {/* Results */}
          <div className="flex-1">
            <div className="flex items-center justify-between mb-6">
              <h2 className="text-xl font-bold text-gray-900">Công ty IT</h2>
              <div className="flex items-center gap-4">
                <div className="flex items-center gap-2">
                  <span className="text-sm text-gray-600">Sort by:</span>
                  <select className="text-sm border-none bg-transparent text-gray-600 focus:ring-0">
                    <option>Most relevant</option>
