import React, { useState, useEffect } from 'react';
import { Link, useLocation } from 'react-router-dom';
import { motion, AnimatePresence } from 'framer-motion';
import { useAuth } from '../../contexts/AuthContext';

interface Message {
  id: number;
  sender: string;
  content: string;
  time: string;
  isRead: boolean;
}

interface Notification {
  id: number;
  title: string;
  content: string;
  time: string;
  isRead: boolean;
}

const Header = () => {
  const location = useLocation();
  const { user, isAuthenticated, logout } = useAuth();
  const [isUserMenuOpen, setIsUserMenuOpen] = useState(false);
  const [isNotificationsOpen, setIsNotificationsOpen] = useState(false);
  const [isMessagesOpen, setIsMessagesOpen] = useState(false);

  // Mock data for messages and notifications
  const messages: Message[] = [
    {
      id: 1,
      sender: 'Công ty ABC',
      content: 'Chúng tôi đã xem xét hồ sơ của bạn và muốn mời bạn phỏng vấn.',
      time: '10 phút trước',
      isRead: false
    },
    {
      id: 2,
      sender: 'HR Manager',
      content: 'Bạn có thể cho chúng tôi biết thời gian phù hợp để phỏng vấn không?',
      time: '1 giờ trước',
      isRead: true
    }
  ];

  const notifications: Notification[] = [
    {
      id: 1,
      title: 'Công việc mới phù hợp',
      content: 'Có 5 công việc mới phù hợp với hồ sơ của bạn',
      time: '5 phút trước',
      isRead: false
    },
    {
      id: 2,
      title: 'Cập nhật trạng thái',
      content: 'Đơn ứng tuyển của bạn đã được xem xét',
      time: '2 giờ trước',
      isRead: true
    }
  ];

  const isActive = (path: string) => {
    return location.pathname === path;
  };

  // Close dropdowns when clicking outside
  useEffect(() => {
    const handleClickOutside = (event: MouseEvent) => {
      const target = event.target as HTMLElement;
      if (!target.closest('.dropdown-container')) {
        setIsUserMenuOpen(false);
        setIsNotificationsOpen(false);
        setIsMessagesOpen(false);
      }
    };

    document.addEventListener('mousedown', handleClickOutside);
    return () => document.removeEventListener('mousedown', handleClickOutside);
  }, []);

  const handleLogout = async () => {
    try {
      await logout();
      setIsUserMenuOpen(false);
    } catch (error) {
      console.error('Logout failed:', error);
    }
  };

  return (
    <header className="bg-white border-b border-gray-200">
      <div className="container mx-auto px-4">
        <div className="flex items-center justify-between h-16">
          {/* Logo */}
          <Link to="/" className="flex items-center">
            <img src="/images/logo.svg" alt="TopJob" className="h-8" />
          </Link>

          {/* Navigation */}
          <nav className="hidden md:flex items-center space-x-8">
            <Link
              to="/jobs"
              className={`text-gray-700 hover:text-emerald-600 transition-colors ${
                isActive('/jobs') ? 'text-emerald-600 font-medium' : ''
              }`}
            >
              Việc làm
            </Link>
            <Link
              to="/companies"
              className={`text-gray-700 hover:text-emerald-600 transition-colors ${
                isActive('/companies') ? 'text-emerald-600 font-medium' : ''
              }`}
            >
              Companies
            </Link>
            <Link
              to="/blog"
              className={`text-gray-700 hover:text-emerald-600 transition-colors ${
                isActive('/blog') ? 'text-emerald-600 font-medium' : ''
              }`}
            >
              Blog IT
            </Link>
            <Link
              to="/contact"
              className={`text-gray-700 hover:text-emerald-600 transition-colors ${
                isActive('/contact') ? 'text-emerald-600 font-medium' : ''
              }`}
            >
              Liên hệ
            </Link>
          </nav>

          {/* Auth Buttons or User Menu */}
          <div className="flex items-center space-x-4">
            {isAuthenticated && user ? (
              <>
                {/* Messages */}
                <div className="relative dropdown-container">
                  <button
                    onClick={() => setIsMessagesOpen(!isMessagesOpen)}
                    className="p-2 text-gray-600 hover:text-emerald-600 relative"
                  >
                    <svg className="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                      <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M8 10h.01M12 10h.01M16 10h.01M9 16H5a2 2 0 01-2-2V6a2 2 0 012-2h14a2 2 0 012 2v8a2 2 0 01-2 2h-5l-5 5v-5z" />
                    </svg>
                    {messages.some(m => !m.isRead) && (
                      <span className="absolute top-0 right-0 block h-4 w-4 rounded-full bg-red-500 text-white text-xs font-medium">
                        {messages.filter(m => !m.isRead).length}
                      </span>
                    )}
                  </button>
                  <AnimatePresence>
                    {isMessagesOpen && (
                      <motion.div
                        initial={{ opacity: 0, y: -10 }}
                        animate={{ opacity: 1, y: 0 }}
                        exit={{ opacity: 0, y: -10 }}
                        className="absolute right-0 mt-2 w-80 bg-white rounded-lg shadow-lg border border-gray-200 z-50"
                      >
                        <div className="p-4 border-b border-gray-200">
                          <h3 className="text-lg font-semibold text-gray-900">Tin nhắn</h3>
                        </div>
                        <div className="max-h-96 overflow-y-auto">
                          {messages.map((message) => (
                            <div
                              key={message.id}
                              className={`p-4 border-b border-gray-100 hover:bg-gray-50 cursor-pointer ${
                                !message.isRead ? 'bg-emerald-50' : ''
                              }`}
                            >
                              <div className="flex items-start justify-between">
                                <div>
                                  <p className="font-medium text-gray-900">{message.sender}</p>
                                  <p className="text-sm text-gray-600 mt-1">{message.content}</p>
                                </div>
                                <span className="text-xs text-gray-500">{message.time}</span>
                              </div>
                            </div>
                          ))}
                        </div>
                        <div className="p-4 border-t border-gray-200">
                          <Link
                            to="/messages"
                            className="block text-center text-emerald-600 hover:text-emerald-700"
                          >
                            Xem tất cả tin nhắn
                          </Link>
                        </div>
                      </motion.div>
                    )}
                  </AnimatePresence>
                </div>

                {/* Notifications */}
                <div className="relative dropdown-container">
                  <button
                    onClick={() => setIsNotificationsOpen(!isNotificationsOpen)}
                    className="p-2 text-gray-600 hover:text-emerald-600 relative"
                  >
                    <svg className="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                      <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M15 17h5l-1.405-1.405A2.032 2.032 0 0118 14.158V11a6.002 6.002 0 00-4-5.659V5a2 2 0 10-4 0v.341C7.67 6.165 6 8.388 6 11v3.159c0 .538-.214 1.055-.595 1.436L4 17h5m6 0v1a3 3 0 11-6 0v-1m6 0H9" />
                    </svg>
                    {notifications.some(n => !n.isRead) && (
                      <span className="absolute top-0 right-0 block h-4 w-4 rounded-full bg-red-500 text-white text-xs font-medium">
                        {notifications.filter(n => !n.isRead).length}
                      </span>
                    )}
                  </button>
                  <AnimatePresence>
                    {isNotificationsOpen && (
                      <motion.div
                        initial={{ opacity: 0, y: -10 }}
                        animate={{ opacity: 1, y: 0 }}
                        exit={{ opacity: 0, y: -10 }}
                        className="absolute right-0 mt-2 w-80 bg-white rounded-lg shadow-lg border border-gray-200 z-50"
                      >
                        <div className="p-4 border-b border-gray-200">
                          <h3 className="text-lg font-semibold text-gray-900">Thông báo</h3>
                        </div>
                        <div className="max-h-96 overflow-y-auto">
                          {notifications.map((notification) => (
                            <div
                              key={notification.id}
                              className={`p-4 border-b border-gray-100 hover:bg-gray-50 cursor-pointer ${
                                !notification.isRead ? 'bg-emerald-50' : ''
                              }`}
                            >
                              <div className="flex items-start justify-between">
                                <div>
                                  <p className="font-medium text-gray-900">{notification.title}</p>
                                  <p className="text-sm text-gray-600 mt-1">{notification.content}</p>
                                </div>
                                <span className="text-xs text-gray-500">{notification.time}</span>
                              </div>
                            </div>
                          ))}
                        </div>
                        <div className="p-4 border-t border-gray-200">
                          <Link
                            to="/notifications"
                            className="block text-center text-emerald-600 hover:text-emerald-700"
                          >
                            Xem tất cả thông báo
                          </Link>
                        </div>
                      </motion.div>
                    )}
                  </AnimatePresence>
                </div>

                {/* User Menu */}
                <div className="relative dropdown-container">
                  <button
                    onClick={() => setIsUserMenuOpen(!isUserMenuOpen)}
                    className="flex items-center space-x-2"
                  >
                    <img
                      src={user.avatar || '/images/default-avatar.png'}
                      alt={user.name}
                      className="w-8 h-8 rounded-full"
                    />
                    <svg className="w-5 h-5 text-gray-400" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                      <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M19 9l-7 7-7-7" />
                    </svg>
                  </button>
                  <AnimatePresence>
                    {isUserMenuOpen && (
                      <motion.div
                        initial={{ opacity: 0, y: -10 }}
                        animate={{ opacity: 1, y: 0 }}
                        exit={{ opacity: 0, y: -10 }}
                        className="absolute right-0 mt-2 w-48 bg-white rounded-lg shadow-lg border border-gray-200 z-50"
                      >
                        <div className="py-2">
                          <Link
                            to="/profile"
                            className="block px-4 py-2 text-gray-700 hover:bg-gray-100"
                          >
                            Trang cá nhân
                          </Link>
                          <Link
                            to="/settings"
                            className="block px-4 py-2 text-gray-700 hover:bg-gray-100"
                          >
                            Cài đặt
                          </Link>
                          <button
                            className="block w-full text-left px-4 py-2 text-gray-700 hover:bg-gray-100"
                            onClick={handleLogout}
                          >
                            Đăng xuất
                          </button>
                        </div>
                      </motion.div>
                    )}
                  </AnimatePresence>
                </div>
              </>
            ) : (
              <>
                <Link
                  to="/login"
                  className="text-gray-700 hover:text-emerald-600 transition-colors"
                >
                  Đăng nhập
                </Link>
                <Link
                  to="/register"
                  className="px-4 py-2 bg-emerald-600 text-white rounded-lg hover:bg-emerald-700 transition-colors"
                >
                  Đăng ký
                </Link>
              </>
            )}
          </div>
        </div>
      </div>
    </header>
  );
};

export default Header;
