export const API_URL = 'http://localhost:3000/api';

export const API_ENDPOINTS = {
  // Auth
  LOGIN: '/auth/login',
  REGISTER: '/auth/register',
  LOGOUT: '/auth/logout',
  REFRESH_TOKEN: '/auth/refresh-token',
  
  // Jobs
  JOBS: '/jobs',
  JOB_DETAIL: (id: string) => `/jobs/${id}`,
  APPLY_JOB: (id: string) => `/jobs/${id}/apply`,
  
  // Companies
  COMPANIES: '/companies',
  COMPANY_DETAIL: (id: string) => `/companies/${id}`,
  
  // Resume
  RESUME: '/resume',
  UPLOAD_RESUME: '/resume/upload',
  
  // Blog
  BLOG_POSTS: '/blog',
  BLOG_DETAIL: (id: string) => `/blog/${id}`,
  
  // Profile
  PROFILE: '/profile',
  UPDATE_PROFILE: '/profile/update',
  UPLOAD_AVATAR: '/profile/avatar',
  
  // Contact
  CONTACT: '/contact',
};
