import React from 'react';
import { useParams } from 'react-router-dom';
import Header from '../components/layout/Header';
import Footer from '../components/layout/Footer';

interface JobDetail {
  id: string;
  title: string;
  company: {
    name: string;
    logo: string;
    location: string;
  };
  type: string;
  postedDate: string;
  salary: string;
  description: string;
  responsibilities: string[];
  whoYouAre: string[];
  niceToHave: string[];
  perks: {
    title: string;
    description: string;
    icon: string;
  }[];
  categories: string[];
  requiredSkills: string[];
}

const mockJobDetail: JobDetail = {
  id: '1',
  title: 'Social Media Assistant',
  company: {
    name: 'Stripe',
    logo: '/company-logos/stripe.png',
    location: 'Paris, France'
  },
  type: 'Full Time',
  postedDate: 'Posted 2 days ago',
  salary: '$2000 - $2500/month',
  description: `We're looking for a Social Media Assistant to join our growing marketing team. You'll be responsible for managing our social media accounts, creating engaging content, and building our online community.`,
  responsibilities: [
    'Create and schedule social media content across multiple platforms (Instagram, Twitter, LinkedIn, etc.)',
    'Monitor and engage with our online community through comments and direct messages',
    'Track social media metrics and create performance reports',
    'Collaborate with the marketing team on social media campaigns',
    'Stay up-to-date with social media trends and best practices'
  ],
  whoYouAre: [
    `You're detail-oriented and creative`,
    `You're a growth marketer and know how to run campaigns`,
    'You have excellent written and verbal communication skills',
    'You have experience with social media management tools',
    'You can work independently and as part of a team'
  ],
  niceToHave: [
    'Experience with graphic design tools (Photoshop, Illustrator, etc.)',
    'Knowledge of SEO and content marketing',
    'Experience with paid social media advertising',
    'Understanding of web analytics tools'
  ],
  perks: [
    {
      title: 'Healthcare',
      description: 'Comprehensive health, dental, and vision coverage for you and your dependents',
      icon: '🏥'
    },
    {
      title: 'Unlimited PTO',
      description: 'Take time off when you need it, we trust you to manage your own time',
      icon: '🌴'
    },
    {
      title: 'Skill Development',
      description: `We invest in your growth with learning stipends and development programs`,
      icon: '📚'
    },
    {
      title: 'Team Summits',
      description: `Travel and connect with your teammates at our annual team gatherings`,
      icon: '✈️'
    },
    {
      title: 'Commuter Benefits',
      description: `We'll help cover your transportation costs to and from the office`,
      icon: '🚌'
    }
  ],
  categories: ['Marketing', 'Social Media', 'Content Creation'],
  requiredSkills: ['Social Media Management', 'Content Creation', 'Analytics', 'Communication', 'Marketing']
};

const JobDetail = () => {
  const { id } = useParams();

  return (
    <div className="min-h-screen bg-gray-50">
      <Header />

      <main className="container mx-auto px-4 py-8">
        {/* Breadcrumb */}
        <div className="flex items-center text-sm text-gray-500 mb-8">
          <a href="/" className="hover:text-indigo-600">Home</a>
          <span className="mx-2">/</span>
          <a href="/jobs" className="hover:text-indigo-600">Jobs</a>
          <span className="mx-2">/</span>
          <span className="text-gray-900">Social Media Assistant</span>
        </div>

        <div className="grid grid-cols-3 gap-8">
          {/* Main Content */}
          <div className="col-span-2 space-y-6">
            {/* Job Header */}
            <div className="bg-white rounded-lg p-6">
              <div className="flex items-start gap-6">
                <img
                  src={mockJobDetail.company.logo}
                  alt={mockJobDetail.company.name}
                  className="w-16 h-16 rounded-lg"
                />
                <div className="flex-1">
                  <div className="flex items-start justify-between">
                    <div>
                      <h1 className="text-2xl font-bold mb-2">{mockJobDetail.title}</h1>
                      <div className="flex items-center gap-4 text-gray-600">
                        <span>{mockJobDetail.company.name}</span>
                        <span>•</span>
                        <span>{mockJobDetail.company.location}</span>
                        <span>•</span>
                        <span>{mockJobDetail.type}</span>
                      </div>
                    </div>
                    <button className="bg-indigo-600 text-white px-6 py-2 rounded-lg hover:bg-indigo-700">
                      Apply Now
                    </button>
                  </div>
                </div>
              </div>
            </div>

            {/* Job Description */}
            <div className="bg-white rounded-lg p-6">
              <h2 className="text-xl font-bold mb-4">Description</h2>
              <p className="text-gray-600 mb-6">{mockJobDetail.description}</p>

              <h3 className="font-semibold mb-3">What you'll do:</h3>
              <ul className="list-disc list-inside space-y-2 text-gray-600 mb-6">
                {mockJobDetail.responsibilities.map((item, index) => (
                  <li key={index}>{item}</li>
                ))}
              </ul>

              <h3 className="font-semibold mb-3">Who you are:</h3>
              <ul className="list-disc list-inside space-y-2 text-gray-600 mb-6">
                {mockJobDetail.whoYouAre.map((item, index) => (
                  <li key={index}>{item}</li>
                ))}
              </ul>

              <h3 className="font-semibold mb-3">Nice to have:</h3>
              <ul className="list-disc list-inside space-y-2 text-gray-600">
                {mockJobDetail.niceToHave.map((item, index) => (
                  <li key={index}>{item}</li>
                ))}
              </ul>
            </div>

            {/* Perks & Benefits */}
            <div className="bg-white rounded-lg p-6">
              <h2 className="text-xl font-bold mb-6">Perks & Benefits</h2>
              <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                {mockJobDetail.perks.map((perk, index) => (
                  <div key={index} className="flex gap-4">
                    <div className="w-12 h-12 bg-indigo-100 rounded-lg flex items-center justify-center">
                      <span className="text-2xl">{perk.icon}</span>
                    </div>
                    <div>
                      <h3 className="font-semibold mb-1">{perk.title}</h3>
                      <p className="text-sm text-gray-600">{perk.description}</p>
                    </div>
                  </div>
                ))}
              </div>
            </div>
          </div>

          {/* Sidebar */}
          <div className="space-y-6">
            {/* Job Overview */}
            <div className="bg-white rounded-lg p-6">
              <h2 className="text-xl font-bold mb-4">Job Overview</h2>
              <div className="space-y-4">
                <div>
                  <h3 className="text-sm text-gray-500 mb-1">Posted Date</h3>
                  <p className="font-medium">{mockJobDetail.postedDate}</p>
                </div>
                <div>
                  <h3 className="text-sm text-gray-500 mb-1">Job Type</h3>
                  <p className="font-medium">{mockJobDetail.type}</p>
                </div>
                <div>
                  <h3 className="text-sm text-gray-500 mb-1">Salary</h3>
                  <p className="font-medium">{mockJobDetail.salary}</p>
                </div>
              </div>
            </div>

            {/* Categories */}
            <div className="bg-white rounded-lg p-6">
              <h2 className="text-xl font-bold mb-4">Categories</h2>
              <div className="flex flex-wrap gap-2">
                {mockJobDetail.categories.map((category, index) => (
                  <span
                    key={index}
                    className="px-3 py-1 bg-gray-100 text-gray-600 rounded-full text-sm"
                  >
                    {category}
                  </span>
                ))}
              </div>
            </div>

            {/* Required Skills */}
            <div className="bg-white rounded-lg p-6">
              <h2 className="text-xl font-bold mb-4">Required Skills</h2>
              <div className="flex flex-wrap gap-2">
                {mockJobDetail.requiredSkills.map((skill, index) => (
                  <span
                    key={index}
                    className="px-3 py-1 bg-gray-100 text-gray-600 rounded-full text-sm"
                  >
                    {skill}
                  </span>
                ))}
              </div>
            </div>
          </div>
        </div>
      </main>

      <Footer />
    </div>
  );
};

export default JobDetail;
