import React from 'react';
import { Link } from 'react-router-dom';

const Header = () => {
  return (
    <header className="bg-white shadow-sm">
      <div className="container mx-auto px-4">
        <div className="flex items-center justify-between h-16">
          {/* Logo */}
          <Link to="/" className="flex items-center gap-2">
            <img src="/images/download.jpg" alt="JobFind" className="h-20 w-auto mt-8" />
          </Link>

          {/* Navigation */}
          <nav className="flex items-center gap-6">
            <Link to="/jobs" className="text-gray-600 hover:text-gray-900">Find Jobs</Link>
            <Link to="/companies/search" className="text-gray-600 hover:text-gray-900">Companies</Link>
            <Link to="/about" className="text-gray-600 hover:text-gray-900">About</Link>
          </nav>

          {/* Auth Buttons */}
          <div className="flex items-center gap-4">
            <Link
              to="/login"
              className="text-gray-600 hover:text-gray-900"
            >
              Sign In
            </Link>
            <Link
              to="/signup"
              className="bg-indigo-600 text-white px-4 py-2 rounded-lg hover:bg-indigo-700 transition-colors"
            >
              Sign Up
            </Link>
          </div>
        </div>
      </div>
    </header>
  );
};

export default Header;
