import { api } from '../utils/api';
import { API_ENDPOINTS } from '../constants/api';

export interface Company {
  id: string;
  name: string;
  logo: string;
  description: string;
  location: string;
  jobCount: number;
  tags: string[];
}

export interface CompanyFilters {
  query?: string;
  location?: string;
  industry?: string;
  page?: number;
  limit?: number;
}

export interface CompanySearchResponse {
  companies: Company[];
  totalPages: number;
}

const companiesService = {
  getCompanies: async (filters?: CompanyFilters) => {
    const response = await api.get(API_ENDPOINTS.COMPANIES, { params: filters });
    return response.data;
  },

  searchCompanies: async (filters: CompanyFilters): Promise<CompanySearchResponse> => {
    const response = await api.get(API_ENDPOINTS.COMPANIES_SEARCH, { params: filters });
    return response.data;
  },

  getCompanyDetail: async (id: string) => {
    const response = await api.get(`${API_ENDPOINTS.COMPANIES}/${id}`);
    return response.data;
  }
};

export default companiesService;
