import React from 'react';
import { Link } from 'react-router-dom';
import Header from '../components/layout/Header';
import Footer from '../components/layout/Footer';

interface Company {
  id: number;
  name: string;
  logo: string;
  description: string;
  jobCount: number;
  tags: string[];
}

const mockCompanies: Company[] = [
  {
    id: 1,
    name: 'Nomad',
    logo: '/images/nomad-logo.svg',
    description: 'Nomad is located in Paris, France. Nomad has generates $728,000 in sales (USD).',
    jobCount: 3,
    tags: ['Business Service']
  },
  {
    id: 2,
    name: 'Discord',
    logo: '/images/discord-logo.svg',
    description: "We'd love to work with someone like you. We care about creating a delightful experience.",
    jobCount: 3,
    tags: ['Business Service']
  },
  {
    id: 3,
    name: 'Maze',
    logo: '/images/maze-logo.svg',
    description: "We're a passionate bunch working from all over the world to build the future of rapid testing together.",
    jobCount: 3,
    tags: ['Business Service']
  },
  {
    id: 4,
    name: 'Udacity',
    logo: '/images/udacity-logo.svg',
    description: 'Udacity is a new type of online university that teaches the actual programming skills.',
    jobCount: 3,
    tags: ['Business Service', 'Technology']
  },
  {
    id: 5,
    name: 'Webflow',
    logo: '/images/webflow-logo.svg',
    description: 'Webflow is the first design and hosting platform built from the ground up for the mobile age.',
    jobCount: 3,
    tags: ['Business Service', 'Technology']
  },
  {
    id: 6,
    name: 'Foundation',
    logo: '/images/foundation-logo.svg',
    description: 'Foundation helps creators mint and auction their digital artworks as NFTs on the Ethereum blockchain.',
    jobCount: 3,
    tags: ['Business Service', 'Crypto']
  }
];

const CompaniesSearch = () => {
  return (
    <>
      <Header />
      <div className="min-h-screen bg-gray-50 py-8">
        <div className="container mx-auto max-w-7xl px-4">
          {/* Header */}
          <div className="mb-8">
            <h1 className="text-3xl font-bold text-gray-900 mb-2">
              Find your <span className="text-emerald-500 relative">
                dream companies
                <span className="absolute bottom-0 left-0 w-full h-1 bg-emerald-500/30"></span>
              </span>
            </h1>
            <p className="text-gray-600">Find the dream companies you dream work for</p>
          </div>

          {/* Search Bar */}
          <div className="bg-white p-4 rounded-lg shadow-sm mb-6">
            <div className="flex gap-4">
              <div className="flex-1 relative">
                <input
                  type="text"
                  placeholder="Company name or keyword"
                  className="w-full pl-10 pr-4 py-2 border border-gray-300 rounded-lg focus:outline-none focus:border-emerald-500"
                />
                <svg className="absolute left-3 top-1/2 -translate-y-1/2 w-5 h-5 text-gray-400" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M21 21l-6-6m2-5a7 7 0 11-14 0 7 7 0 0114 0z" />
                </svg>
              </div>
              <div className="flex-1 relative">
                <input
                  type="text"
                  placeholder="TPHCM"
                  className="w-full pl-10 pr-4 py-2 border border-gray-300 rounded-lg focus:outline-none focus:border-emerald-500"
                />
                <svg className="absolute left-3 top-1/2 -translate-y-1/2 w-5 h-5 text-gray-400" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M17.657 16.657L13.414 20.9a1.998 1.998 0 01-2.827 0l-4.244-4.243a8 8 0 1111.314 0z" />
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M15 11a3 3 0 11-6 0 3 3 0 016 0z" />
                </svg>
              </div>
              <button className="px-8 py-2 bg-emerald-600 text-white rounded-lg hover:bg-emerald-700 transition-colors">
                Search
              </button>
            </div>
            <div className="flex gap-2 mt-4">
              <span className="text-sm text-gray-500">Popular:</span>
              {['Twitter', 'Microsoft', 'Apple', 'Facebook'].map((company) => (
                <Link
                  key={company}
                  to={`/companies/search?q=${company}`}
                  className="text-sm text-emerald-600 hover:text-emerald-700"
                >
                  {company}
                </Link>
              ))}
            </div>
          </div>

          {/* Companies Section */}
          <div>
            <h2 className="text-xl font-bold text-gray-900 mb-4">Công ty IT</h2>
            <p className="text-gray-600 mb-8">Dựa trên hồ sơ của bạn, số thích công ty và hoạt động gần đây</p>

            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
              {mockCompanies.map((company) => (
                <Link 
                  key={company.id}
                  to={`/companies/${company.id}`}
                  className="block bg-white rounded-lg border border-gray-200 p-6 hover:border-emerald-500 transition-colors"
                >
                  <div className="flex items-start justify-between mb-4">
                    <img src={company.logo} alt={company.name} className="w-12 h-12 rounded-lg" />
                    <span className="text-sm text-gray-500">{company.jobCount} Jobs</span>
                  </div>
                  <h3 className="text-lg font-semibold text-gray-900 mb-2">{company.name}</h3>
                  <p className="text-sm text-gray-600 mb-4 line-clamp-2">{company.description}</p>
                  <div className="flex flex-wrap gap-2">
                    {company.tags.map((tag) => (
                      <span 
                        key={tag} 
                        className={`px-3 py-1 rounded-full text-sm ${
                          tag === 'Crypto' 
                            ? 'bg-purple-50 text-purple-600' 
                            : tag === 'Technology'
                            ? 'bg-orange-50 text-orange-600'
                            : 'bg-gray-100 text-gray-600'
                        }`}
                      >
                        {tag}
                      </span>
                    ))}
                  </div>
                </Link>
              ))}
            </div>

            {/* Pagination */}
            <div className="flex justify-center items-center gap-2 mt-8">
              <button className="p-2 rounded hover:bg-gray-100">
                <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M15 19l-7-7 7-7" />
                </svg>
              </button>
              {[1, 2, 3, 4, 5].map((page) => (
                <button
                  key={page}
                  className={`w-8 h-8 rounded ${
                    page === 1 ? 'bg-emerald-600 text-white' : 'hover:bg-gray-100'
                  }`}
                >
                  {page}
                </button>
              ))}
              <span>...</span>
              <button className="w-8 h-8 rounded hover:bg-gray-100">33</button>
              <button className="p-2 rounded hover:bg-gray-100">
                <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 5l7 7-7 7" />
                </svg>
              </button>
            </div>
          </div>
        </div>
      </div>
      <Footer />
    </>
  );
};

export default CompaniesSearch;
