import api from './api';
import { API_ENDPOINTS } from '../config/api';

export interface Company {
  id: string;
  name: string;
  logo: string;
  description: string;
  location: string;
  website: string;
  size: string;
  industry: string;
  founded: string;
  jobs: {
    id: string;
    title: string;
    location: string;
    type: string;
  }[];
}

export interface CompanyFilters {
  search?: string;
  industry?: string;
  location?: string;
  size?: string;
  page?: number;
  limit?: number;
}

const companiesService = {
  getCompanies: async (filters?: CompanyFilters): Promise<{ companies: Company[]; total: number }> => {
    const response = await api.get(API_ENDPOINTS.COMPANIES, { params: filters });
    return response.data;
  },

  getCompanyDetail: async (id: string): Promise<Company> => {
    const response = await api.get(API_ENDPOINTS.COMPANY_DETAIL(id));
    return response.data;
  },
};

export default companiesService;
