import React, { useState } from 'react';
import { Link } from 'react-router-dom';

interface Company {
  id: string;
  name: string;
  logo: string;
  description: string;
  jobCount: number;
  tags: string[];
}

interface Category {
  id: string;
  name: string;
  icon: React.ReactNode;
  companiesCount?: number;
}

const BrowseCompanies = () => {
  const [searchQuery, setSearchQuery] = useState('');
  const [selectedLocation, setSelectedLocation] = useState('');

  const recommendedCompanies: Company[] = [
    {
      id: '1',
      name: 'Nomad',
      logo: '/company-logos/nomad.png',
      description: 'Nomad is located in Paris, France. Nomad has generates $725,000 in sales (USD).',
      jobCount: 3,
      tags: ['Business Service']
    },
    {
      id: '2',
      name: 'Discord',
      logo: '/company-logos/discord.png',
      description: "We'd love to work with someone like you. We care about creating a delightful experience.",
      jobCount: 3,
      tags: ['Business Service']
    },
    {
      id: '3',
      name: 'Maze',
      logo: '/company-logos/maze.png',
      description: "We're a passionate bunch working from all over the world to build the future of rapid testing together.",
      jobCount: 3,
      tags: ['Business Service']
    },
    {
      id: '4',
      name: 'Udacity',
      logo: '/company-logos/udacity.png',
      description: 'Udacity is a new type of online university that teaches the actual programming skills.',
      jobCount: 3,
      tags: ['Business Service']
    },
    {
      id: '5',
      name: 'Webflow',
      logo: '/company-logos/webflow.png',
      description: 'Webflow is the first design and hosting platform built from the ground up for the mobile age.',
      jobCount: 3,
      tags: ['Business Service', 'Technology']
    },
    {
      id: '6',
      name: 'Foundation',
      logo: '/company-logos/foundation.png',
      description: 'Foundation helps creators mint and auction their digital artworks as NFTs on the Ethereum blockchain.',
      jobCount: 3,
      tags: ['Business Service', 'Crypto']
    }
  ];

  const categories: Category[] = [
    {
      id: 'design',
      name: 'Design',
      icon: (
        <svg className="w-8 h-8" fill="none" viewBox="0 0 24 24" stroke="currentColor">
          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M11 5H6a2 2 0 00-2 2v11a2 2 0 002 2h11a2 2 0 002-2v-5m-1.414-9.414a2 2 0 112.828 2.828L11.828 15H9v-2.828l8.586-8.586z" />
        </svg>
      ),
      companiesCount: 24
    },
    {
      id: 'fintech',
      name: 'Fintech',
      icon: (
        <svg className="w-8 h-8" fill="none" viewBox="0 0 24 24" stroke="currentColor">
          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 8c-1.657 0-3 .895-3 2s1.343 2 3 2 3 .895 3 2-1.343 2-3 2m0-8c1.11 0 2.08.402 2.599 1M12 8V7m0 1v8m0 0v1m0-1c-1.11 0-2.08-.402-2.599-1M21 12a9 9 0 11-18 0 9 9 0 0118 0z" />
        </svg>
      )
    },
    {
      id: 'hosting',
      name: 'Hosting',
      icon: (
        <svg className="w-8 h-8" fill="none" viewBox="0 0 24 24" stroke="currentColor">
          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M5 12h14M5 12a2 2 0 01-2-2V6a2 2 0 012-2h14a2 2 0 012 2v4a2 2 0 01-2 2M5 12a2 2 0 00-2 2v4a2 2 0 002 2h14a2 2 0 002-2v-4a2 2 0 00-2-2m-2-4h.01M17 16h.01" />
        </svg>
      )
    },
    {
      id: 'business',
      name: 'Business Service',
      icon: (
        <svg className="w-8 h-8" fill="none" viewBox="0 0 24 24" stroke="currentColor">
          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M21 13.255A23.931 23.931 0 0112 15c-3.183 0-6.22-.62-9-1.745M16 6V4a2 2 0 00-2-2h-4a2 2 0 00-2 2v2m4 6h.01M5 20h14a2 2 0 002-2V8a2 2 0 00-2-2H5a2 2 0 00-2 2v10a2 2 0 002 2z" />
        </svg>
      )
    },
    {
      id: 'development',
      name: 'Development',
      icon: (
        <svg className="w-8 h-8" fill="none" viewBox="0 0 24 24" stroke="currentColor">
          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M10 20l4-16m4 4l4 4-4 4M6 16l-4-4 4-4" />
        </svg>
      )
    }
  ];

  const designCompanies: Company[] = [
    {
      id: 'pentagram',
      name: 'Pentagram',
      logo: '/company-logos/pentagram.png',
      description: '',
      jobCount: 3,
      tags: []
    },
    {
      id: 'wolff-olins',
      name: 'Wolff Olins',
      logo: '/company-logos/wolff-olins.png',
      description: '',
      jobCount: 3,
      tags: []
    },
    {
      id: 'clay',
      name: 'Clay',
      logo: '/company-logos/clay.png',
      description: '',
      jobCount: 3,
      tags: []
    },
    {
      id: 'mediamonks',
      name: 'MediaMonks',
      logo: '/company-logos/mediamonks.png',
      description: '',
      jobCount: 3,
      tags: []
    },
    {
      id: 'packer',
      name: 'Packer',
      logo: '/company-logos/packer.png',
      description: '',
      jobCount: 3,
      tags: []
    },
    {
      id: 'square',
      name: 'Square',
      logo: '/company-logos/square.png',
      description: '',
      jobCount: 3,
      tags: []
    },
    {
      id: 'divy',
      name: 'Divy',
      logo: '/company-logos/divy.png',
      description: '',
      jobCount: 3,
      tags: []
    },
    {
      id: 'webflow',
      name: 'WebFlow',
      logo: '/company-logos/webflow.png',
      description: '',
      jobCount: 3,
      tags: []
    }
  ];

  return (
    <div className="min-h-screen bg-white">
      <main className="container mx-auto px-4 py-8">
        {/* Hero Section */}
        <section className="text-center mb-12">
          <h1 className="text-4xl font-bold mb-2">
            Find Your Dream Company
          </h1>
          <p className="text-xl text-gray-600">
            Browse through thousands of companies and find the perfect match
          </p>
        </section>

        {/* Search and Filter */}
        <div className="flex flex-col md:flex-row gap-4 mb-8">
          <div className="flex-1">
            <input
              type="text"
              placeholder="Search companies..."
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
              className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-emerald-500 focus:border-emerald-500"
            />
          </div>
          <div className="w-full md:w-64">
            <select
              value={selectedLocation}
              onChange={(e) => setSelectedLocation(e.target.value)}
              className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-emerald-500 focus:border-emerald-500"
            >
              <option value="">All Locations</option>
              <option value="remote">Remote</option>
              <option value="us">United States</option>
              <option value="uk">United Kingdom</option>
              <option value="eu">Europe</option>
              <option value="asia">Asia</option>
            </select>
          </div>
        </div>

        {/* Categories */}
        <div className="grid grid-cols-2 md:grid-cols-5 gap-4 mb-12">
          {categories.map((category) => (
            <div
              key={category.id}
              className="flex flex-col items-center p-4 bg-gray-50 rounded-lg hover:bg-gray-100 cursor-pointer transition-colors"
            >
              <div className="text-emerald-600 mb-2">{category.icon}</div>
              <h3 className="font-medium text-gray-900">{category.name}</h3>
              {category.companiesCount && (
                <p className="text-sm text-gray-500">{category.companiesCount} companies</p>
              )}
            </div>
          ))}
        </div>

        {/* Recommended Companies */}
        <section className="mb-12">
          <h2 className="text-2xl font-bold mb-6">Recommended Companies</h2>
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
            {recommendedCompanies.map((company) => (
              <Link
                key={company.id}
                to={`/companies/${company.id}`}
                className="flex flex-col bg-white rounded-lg shadow-sm hover:shadow-md transition-shadow p-6"
              >
                <div className="flex items-center mb-4">
                  <img
                    src={company.logo}
                    alt={company.name}
                    className="w-12 h-12 object-contain mr-4"
                  />
                  <div>
                    <h3 className="font-semibold text-lg">{company.name}</h3>
                    <p className="text-sm text-gray-500">{company.jobCount} jobs</p>
                  </div>
                </div>
                <p className="text-gray-600 mb-4">{company.description}</p>
                <div className="flex flex-wrap gap-2">
                  {company.tags.map((tag) => (
                    <span
                      key={tag}
                      className="px-2 py-1 bg-gray-100 text-gray-600 rounded-full text-sm"
                    >
                      {tag}
                    </span>
                  ))}
                </div>
              </Link>
            ))}
          </div>
        </section>

        {/* Design Companies */}
        <section>
          <h2 className="text-2xl font-bold mb-6">Design Companies</h2>
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
            {designCompanies.map((company) => (
              <Link
                key={company.id}
                to={`/companies/${company.id}`}
                className="flex flex-col items-center bg-white rounded-lg shadow-sm hover:shadow-md transition-shadow p-6"
              >
                <img
                  src={company.logo}
                  alt={company.name}
                  className="w-16 h-16 object-contain mb-4"
                />
                <h3 className="font-semibold text-lg text-center">{company.name}</h3>
                <p className="text-sm text-gray-500">{company.jobCount} jobs</p>
              </Link>
            ))}
          </div>
        </section>
      </main>
    </div>
  );
};

export default BrowseCompanies;
