import React from 'react';
import { Link } from 'react-router-dom';
import { motion } from 'framer-motion';

interface CategoryCardProps {
  title: string;
  icon: string;
  count: number;
  color: string;
}

const CategoryCard: React.FC<CategoryCardProps> = ({ title, icon, count, color }) => {
  return (
    <motion.div
      whileHover={{ scale: 1.02 }}
      whileTap={{ scale: 0.98 }}
      className={`p-6 rounded-lg ${color} hover:shadow-lg transition-shadow`}
    >
      <div className="flex items-center justify-between mb-4">
        <span className="text-3xl">{icon}</span>
        <span className="text-sm font-medium">{count} jobs</span>
      </div>
      <h3 className="text-lg font-semibold mb-2">{title}</h3>
      <Link to={`/jobs?category=${title.toLowerCase()}`} className="text-sm hover:underline">
        View all jobs â†’
      </Link>
    </motion.div>
  );
};

export default CategoryCard;
