import api from './api';
import { API_ENDPOINTS } from '../config/api';

export interface Job {
  id: string;
  title: string;
  company: {
    id: string;
    name: string;
    logo: string;
  };
  location: string;
  salary: {
    min: number;
    max: number;
    currency: string;
  };
  type: string;
  description: string;
  requirements: string[];
  benefits: string[];
  createdAt: string;
  deadline: string;
}

export interface JobFilters {
  search?: string;
  location?: string;
  type?: string;
  salary?: {
    min?: number;
    max?: number;
  };
  page?: number;
  limit?: number;
}

const jobsService = {
  getJobs: async (filters?: JobFilters): Promise<{ jobs: Job[]; total: number }> => {
    const response = await api.get(API_ENDPOINTS.JOBS, { params: filters });
    return response.data;
  },

  getJobDetail: async (id: string): Promise<Job> => {
    const response = await api.get(API_ENDPOINTS.JOB_DETAIL(id));
    return response.data;
  },

  applyJob: async (id: string): Promise<void> => {
    await api.post(API_ENDPOINTS.APPLY_JOB(id));
  },
};

export default jobsService;
