import React from 'react';
import { Link } from 'react-router-dom';
import Header from '../components/layout/Header';
import Footer from '../components/layout/Footer';

interface Company {
  id: number;
  name: string;
  logo: string;
  description: string;
  jobCount: number;
  tags: string[];
}

const mockCompanies: Company[] = [
  {
    id: 1,
    name: 'Nomad',
    logo: '/images/download.jpg',
    description: 'A leading tech company specializing in remote work solutions',
    jobCount: 3,
    tags: ['Technology', 'Remote Work']
  },
  {
    id: 2,
    name: 'Discord',
    logo: '/images/download.jpg',
    description: "We'd love to work with someone like you. We care about creating a delightful experience.",
    jobCount: 3,
    tags: ['Technology', 'Communication']
  },
  {
    id: 3,
    name: 'Maze',
    logo: '/images/download.jpg',
    description: "We're a passionate bunch working from all over the world to build the future of rapid testing together.",
    jobCount: 3,
    tags: ['Technology', 'Testing']
  },
  {
    id: 4,
    name: 'Udacity',
    logo: '/images/download.jpg',
    description: 'Udacity is a new type of online university that teaches the actual programming skills.',
    jobCount: 3,
    tags: ['Education', 'Technology']
  },
  {
    id: 5,
    name: 'Webflow',
    logo: '/images/download.jpg',
    description: 'Webflow is the first design and hosting platform built from the ground up for the mobile age.',
    jobCount: 3,
    tags: ['Design', 'Technology']
  },
  {
    id: 6,
    name: 'Foundation',
    logo: '/images/download.jpg',
    description: 'Foundation helps creators mint and auction their digital artworks as NFTs on the Ethereum blockchain.',
    jobCount: 3,
    tags: ['Crypto', 'Technology']
  }
];

const Companies = () => {
  return (
    <>
      <Header />
      <div className="min-h-screen bg-gray-50 py-8">
        <div className="container mx-auto px-4">
          <div className="flex justify-between items-center mb-8">
            <h1 className="text-3xl font-bold text-gray-900">IT Companies</h1>
            <Link 
              to="/companies/search" 
              className="px-4 py-2 bg-emerald-600 text-white rounded-lg hover:bg-emerald-700 transition-colors"
            >
              Search Companies
            </Link>
          </div>

          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
            {mockCompanies.map((company) => (
              <div key={company.id} className="bg-white rounded-lg shadow-md overflow-hidden">
                <div className="p-6">
                  <div className="flex items-center gap-4 mb-4">
                    <img 
                      src={company.logo} 
                      alt={company.name} 
                      className="w-16 h-16 rounded-lg object-cover"
                    />
                    <div>
                      <h3 className="text-xl font-semibold text-gray-900">{company.name}</h3>
                      <p className="text-gray-600">{company.jobCount} Jobs</p>
                    </div>
                  </div>
                  <p className="text-gray-600 mb-4">
                    {company.description}
                  </p>
                  <div className="flex flex-wrap gap-2 mb-4">
                    {company.tags.map((tag) => (
                      <span 
                        key={tag}
                        className={`px-3 py-1 rounded-full text-sm ${
                          tag === 'Crypto' 
                            ? 'bg-purple-100 text-purple-800' 
                            : tag === 'Technology'
                            ? 'bg-orange-100 text-orange-800'
                            : 'bg-emerald-100 text-emerald-800'
                        }`}
                      >
                        {tag}
                      </span>
                    ))}
                  </div>
                  <Link 
                    to={`/companies/${company.id}`}
                    className="block w-full text-center px-4 py-2 bg-emerald-600 text-white rounded-lg hover:bg-emerald-700 transition-colors"
                  >
                    View Profile
                  </Link>
                </div>
              </div>
            ))}
          </div>
        </div>
      </div>
      <Footer />
    </>
  );
};

export default Companies;
