import React from 'react';
import { useParams } from 'react-router-dom';
import Header from '../components/layout/Header';
import Footer from '../components/layout/Footer';

interface JobDetail {
  id: string;
  title: string;
  company: {
    name: string;
    logo: string;
    location: string;
  };
  type: string;
  postedDate: string;
  salary: string;
  description: string;
  responsibilities: string[];
  whoYouAre: string[];
  niceToHave: string[];
  perks: {
    title: string;
    description: string;
    icon: string;
  }[];
  categories: string[];
  requiredSkills: string[];
}

const mockJobDetail: JobDetail = {
  id: '1',
  title: 'Social Media Assistant',
  company: {
    name: 'Stripe',
    logo: '/company-logos/stripe.png',
    location: 'Paris, France'
  },
  type: 'Full-Time',
  postedDate: 'July 31, 2021',
  salary: '$135-165k USD',
  description: 'Stripe is looking for Social Media Marketing expert to help manage our online networks. You will be responsible for monitoring our social media channels, creating content, finding effective ways to engage the community and maximize others to engage on our channels.',
  responsibilities: [
    'Community engagement to ensure that is supported and actively represented online',
    'Focus on social media content development and publication',
    'Marketing and strategy support',
    'Stay on top of trends on social media platforms and suggest content ideas to the team',
    'Engage with online communities'
  ],
  whoYouAre: [
    'You get energy from people and building the ideal work environment',
    'You have a sense for beautiful spaces and office experiences',
    'You are a confident office manager, ready for added responsibilities',
    `You're detail-oriented and creative`,
    `You're a growth marketer and know how to run campaigns`
  ],
  niceToHave: [
    'Fluent in English',
    'Project management skills',
    'Copy editing skills'
  ],
  perks: [
    {
      title: 'Full Healthcare',
      description: `We believe in thriving communities and that starts with our team being happy and healthy.`,
      icon: 'healthcare'
    },
    {
      title: 'Unlimited Vacation',
      description: `We believe you should have a flexible schedule that makes room for family, wellness, and fun.`,
      icon: 'vacation'
    },
    {
      title: 'Skill Development',
      description: `We believe in always learning and leveling up our skills. Whether it's a conference or online course, we got you covered.`,
      icon: 'education'
    },
    {
      title: 'Team Summits',
      description: `Every 6 months we have a full team summit where we have fun, reflect, and plan for the coming quarter.`,
      icon: 'team'
    },
    {
      title: 'Remote Working',
      description: `You know how you work best and we want to support that. Work from home, a cafe, or anywhere else you feel productive.`,
      icon: 'remote'
    },
    {
      title: 'Commuter Benefits',
      description: `We're grateful for the time and energy you spend getting to work every day.`,
      icon: 'commuter'
    }
  ],
  categories: ['Marketing', 'Design'],
  requiredSkills: ['Project Management', 'Copywriting', 'Social Media Marketing', 'English']
};

const JobDetail = () => {
  const { id } = useParams();

  return (
    <div className="min-h-screen bg-gray-50">
      <Header />

      <main className="container mx-auto px-4 py-8">
        {/* Breadcrumb */}
        <div className="flex items-center text-sm text-gray-500 mb-8">
          <a href="/" className="hover:text-indigo-600">Home</a>
          <span className="mx-2">/</span>
          <a href="/jobs" className="hover:text-indigo-600">Jobs</a>
          <span className="mx-2">/</span>
          <span className="text-gray-900">Social Media Assistant</span>
        </div>

        {/* Job Header */}
        <div className="bg-white rounded-lg p-6 mb-8 flex items-start justify-between">
          <div className="flex items-start gap-6">
            <img
              src={mockJobDetail.company.logo}
              alt={mockJobDetail.company.name}
              className="w-16 h-16 rounded-lg"
            />
            <div>
              <h1 className="text-2xl font-bold mb-2">{mockJobDetail.title}</h1>
              <div className="flex items-center gap-4 text-gray-600">
                <span>{mockJobDetail.company.name}</span>
                <span>‚Ä¢</span>
                <span>{mockJobDetail.company.location}</span>
                <span>‚Ä¢</span>
                <span>{mockJobDetail.type}</span>
              </div>
            </div>
          </div>
          <button className="bg-indigo-600 text-white px-6 py-2 rounded-lg hover:bg-indigo-700">
            Apply
          </button>
        </div>

        {/* Job Details */}
        <div className="grid grid-cols-3 gap-8">
          <div className="col-span-2">
            {/* About this role */}
            <div className="bg-white rounded-lg p-6 mb-8">
              <h2 className="text-xl font-bold mb-4">Description</h2>
              <p className="text-gray-600 mb-6">{mockJobDetail.description}</p>

              <h2 className="text-xl font-bold mb-4">Responsibilities</h2>
              <ul className="list-disc list-inside text-gray-600 mb-6">
                {mockJobDetail.responsibilities.map((item, index) => (
                  <li key={index} className="mb-2">{item}</li>
                ))}
              </ul>

              <h2 className="text-xl font-bold mb-4">Who You Are</h2>
              <ul className="list-disc list-inside text-gray-600 mb-6">
                {mockJobDetail.whoYouAre.map((item, index) => (
                  <li key={index} className="mb-2">{item}</li>
                ))}
              </ul>

              <h2 className="text-xl font-bold mb-4">Nice-To-Haves</h2>
              <ul className="list-disc list-inside text-gray-600">
                {mockJobDetail.niceToHave.map((item, index) => (
                  <li key={index} className="mb-2">{item}</li>
                ))}
              </ul>
            </div>

            {/* Perks & Benefits */}
            <div className="bg-white rounded-lg p-6">
              <h2 className="text-xl font-bold mb-6">Perks & Benefits</h2>
              <div className="grid grid-cols-2 gap-6">
                {mockJobDetail.perks.map((perk, index) => (
                  <div key={index} className="flex gap-4">
                    <div className="w-12 h-12 bg-indigo-100 rounded-lg flex items-center justify-center">
                      <span className="text-indigo-600 text-xl">
                        {/* You can replace this with actual icons */}
                        {perk.icon === 'healthcare' && 'üè•'}
                        {perk.icon === 'vacation' && 'üå¥'}
                        {perk.icon === 'education' && 'üìö'}
                        {perk.icon === 'team' && 'üë•'}
                        {perk.icon === 'remote' && 'üè†'}
                        {perk.icon === 'commuter' && 'üöå'}
                      </span>
                    </div>
                    <div>
                      <h3 className="font-semibold mb-1">{perk.title}</h3>
                      <p className="text-sm text-gray-600">{perk.description}</p>
                    </div>
                  </div>
                ))}
              </div>
            </div>
          </div>

          {/* Sidebar */}
          <div className="space-y-6">
            {/* About this role */}
            <div className="bg-white rounded-lg p-6">
              <h2 className="text-xl font-bold mb-4">About this role</h2>
              <div className="space-y-4">
                <div>
                  <h3 className="text-sm text-gray-500 mb-1">Applied</h3>
                  <p className="font-medium">14 Candidates</p>
                </div>
                <div>
                  <h3 className="text-sm text-gray-500 mb-1">Job Posted On</h3>
                  <p className="font-medium">{mockJobDetail.postedDate}</p>
                </div>
                <div>
                  <h3 className="text-sm text-gray-500 mb-1">Job Type</h3>
                  <p className="font-medium">{mockJobDetail.type}</p>
                </div>
                <div>
                  <h3 className="text-sm text-gray-500 mb-1">Salary</h3>
                  <p className="font-medium">{mockJobDetail.salary}</p>
                </div>
              </div>
            </div>

            {/* Categories */}
            <div className="bg-white rounded-lg p-6">
              <h2 className="text-xl font-bold mb-4">Categories</h2>
              <div className="flex flex-wrap gap-2">
                {mockJobDetail.categories.map((category, index) => (
                  <span
                    key={index}
                    className="px-3 py-1 bg-gray-100 text-gray-600 rounded-full text-sm"
                  >
                    {category}
                  </span>
                ))}
              </div>
            </div>

            {/* Required Skills */}
            <div className="bg-white rounded-lg p-6">
              <h2 className="text-xl font-bold mb-4">Required Skills</h2>
              <div className="flex flex-wrap gap-2">
                {mockJobDetail.requiredSkills.map((skill, index) => (
                  <span
                    key={index}
                    className="px-3 py-1 bg-gray-100 text-gray-600 rounded-full text-sm"
                  >
                    {skill}
                  </span>
                ))}
              </div>
            </div>
          </div>
        </div>
      </main>

      <Footer />
    </div>
  );
};

export default JobDetail;
